from typing import (
    Optional,
    Any,
)

from zope.interface import Interface


class ILoggingContext(Interface):

    def logPrefix() -> str:
        pass


class IProtocol(Interface):
    def dataReceived(data: bytes) -> None:
        pass

    def connectionLost(reason: Any) -> None:
        pass

    def makeConnection(transport: Any) -> None:
        pass

    def connectionMade() -> None:
        pass


class IProtocolFactory(Interface):

    def buildProtocol(addr: Any) -> Optional[IProtocol]:
        pass

    def doStart() -> None:
        pass

    def doStop() -> None:
        pass
